<?php    //This file creates an array called $weeks that contains the date for    //each Wednesday of the semester.  It compensates for Thanksgiving break    //and Spring break.    //Ideas to figure out how to use this over multiple years/semesters    //$baseDates['2010f'] = strtotime('2010-08-25');    //$baseDates['2010s'] = strtotime('2011-01-19');    //The first Wednesday (Bible study day) of each semester    $baseDateFall = strtotime('2017-08-30');    $baseDateSpring = strtotime('2018-01-17');    $_REQUEST['sem'] == 'spring' ? $sem = 'spring' : $sem = 'fall';    for ($i=0;$i<15; $i++) {        if ($sem == 'fall') {            $week = $i < 13 ? $i : $i + 1;            $date = strtotime( date("m/d/y", $baseDateFall) . " + " . $week . " weeks");        } else {            $week = $i < 7 ? $i : $i + 1;            $date = strtotime( date("m/d/y", $baseDateSpring) . " + " . $week . " weeks");        }        $weeks[$i] = $date;    }?>